#include<iostream>
using namespace std;
char input[1001] = "";
int product[1020] ;

void putZero(int pos){
    for (int i = pos  ; i < pos+9;i++)
        product[i] = 0;
}
int getProduct(int pos){
    return (input[pos]-'0') * ( input[pos+1]  - '0') * (  input[pos+2] - '0') * (  input[pos+3]  - '0') * (  input[pos+4] - '0') ;
    
}
int main (){
    int max = 0;
    for(int i=0;i<=1008; i++)
        product[i]=-1;
    
    for(int i=0;i<1000;i++){
        if((input[i] - '0') == 0)
            putZero(i);
    }
    for(int i=4;i<1004;i++){
        
        if (product[i] !=0)
            product[i] = getProduct(i-4);
        max = (product[i] > max )? product[i]:max;
        
    }
    for(int i=0;i<1000;i++){
        cout<<product[i]<<" ";
    }
    cout<<"\n"<<max<<"\n";
    return 0;
}